import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PropertyCard from '../components/PropertyCard';
import RequestModal from '../components/RequestModal';

const BrowseProperties = () => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [modalOpen, setModalOpen] = useState(false);
    const [selectedProperty, setSelectedProperty] = useState(null);
    const [requestLoading, setRequestLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchProperties();
    }, []);

    const fetchProperties = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/properties');
            setProperties(res.data);
            setLoading(false);
        } catch (err) {
            console.error(err);
            setLoading(false);
        }
    };

    const handleRequestClick = (propertyId) => {
        const property = properties.find(p => p._id === propertyId);
        setSelectedProperty(property);
        setModalOpen(true);
        setSuccessMessage('');
        setErrorMessage('');
    };

    const handleRequestSubmit = async (message) => {
        if (!selectedProperty) return;

        setRequestLoading(true);
        setErrorMessage('');

        try {
            const token = localStorage.getItem('token');
            await axios.post(
                'http://localhost:5000/api/requests',
                { propertyId: selectedProperty._id, message },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setSuccessMessage('Request sent successfully!');
            setTimeout(() => {
                setModalOpen(false);
                setSuccessMessage('');
                setSelectedProperty(null);
            }, 1500);
        } catch (err) {
            setErrorMessage(err.response?.data?.message || 'Error sending request. Please try again.');
            setTimeout(() => {
                setErrorMessage('');
            }, 4000);
        } finally {
            setRequestLoading(false);
        }
    };

    const handleCloseModal = () => {
        if (!requestLoading) {
            setModalOpen(false);
            setSelectedProperty(null);
            setErrorMessage('');
            setSuccessMessage('');
        }
    };

    if (loading) return <div className="dashboard-container"><div className="loading">Loading...</div></div>;

    return (
        <>
            <div className="dashboard-container">
                <div className="dashboard-header">
                    <div>
                        <h1>Available Properties</h1>
                        <p>Browse through our selection of verified rental properties</p>
                    </div>
                </div>
                {properties.length === 0 ? (
                    <div className="empty-state">
                        <h3>No properties available</h3>
                        <p>Check back later for new listings.</p>
                    </div>
                ) : (
                    <div className="properties-grid">
                        {properties.map(property => (
                            <PropertyCard
                                key={property._id}
                                property={property}
                                onRequest={handleRequestClick}
                            />
                        ))}
                    </div>
                )}
            </div>

            <RequestModal
                isOpen={modalOpen}
                onClose={handleCloseModal}
                onSubmit={handleRequestSubmit}
                propertyTitle={selectedProperty?.title || ''}
                loading={requestLoading}
            />

            {successMessage && (
                <div className="toast toast-success">
                    {successMessage}
                </div>
            )}

            {errorMessage && (
                <div className="toast toast-error">
                    {errorMessage}
                </div>
            )}
        </>
    );
};

export default BrowseProperties;
