import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import PropertyCard from '../components/PropertyCard';
import RequestModal from '../components/RequestModal';
import { FiSearch, FiFilter, FiX, FiZap, FiKey, FiDroplet, FiMapPin } from 'react-icons/fi';

const BrowseProperties = () => {
    const [properties, setProperties] = useState([]);
    const [loading, setLoading] = useState(true);
    const [modalOpen, setModalOpen] = useState(false);
    const [selectedProperty, setSelectedProperty] = useState(null);
    const [requestLoading, setRequestLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    // Filter and search states
    const [searchQuery, setSearchQuery] = useState('');
    const [filters, setFilters] = useState({
        propertyType: 'any',
        bedrooms: '',
        minPrice: '',
        maxPrice: '',
        city: '',
        status: 'available',
        amenities: {
            instantBook: false,
            selfCheckIn: false,
            pool: false,
            parking: false
        }
    });
    const [sortBy, setSortBy] = useState('newest');
    const [showFilters, setShowFilters] = useState(false);

    // Calculate price range from properties
    const priceRange = useMemo(() => {
        if (properties.length === 0) return { min: 0, max: 100000 };
        const prices = properties.map(p => p.price);
        return {
            min: Math.min(...prices),
            max: Math.max(...prices)
        };
    }, [properties]);

    useEffect(() => {
        fetchProperties();
    }, []);

    const fetchProperties = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/properties');
            setProperties(res.data);
            setLoading(false);
        } catch (err) {
            console.error(err);
            setLoading(false);
        }
    };

    // Get unique cities for filter dropdown
    const cities = useMemo(() => {
        const citySet = new Set(properties.map(p => p.city).filter(Boolean));
        return Array.from(citySet).sort();
    }, [properties]);

    // Filter and sort properties
    const filteredAndSortedProperties = useMemo(() => {
        let filtered = [...properties];

        // Search filter
        if (searchQuery) {
            const query = searchQuery.toLowerCase();
            filtered = filtered.filter(property =>
                property.title?.toLowerCase().includes(query) ||
                property.description?.toLowerCase().includes(query) ||
                property.address?.toLowerCase().includes(query) ||
                property.city?.toLowerCase().includes(query)
            );
        }

        // Property type filter
        if (filters.propertyType && filters.propertyType !== 'any') {
            filtered = filtered.filter(p => p.propertyType === filters.propertyType);
        }

        // Bedrooms filter
        if (filters.bedrooms) {
            filtered = filtered.filter(p => p.bedrooms === parseInt(filters.bedrooms));
        }

        // Price range filter
        if (filters.minPrice) {
            filtered = filtered.filter(p => p.price >= parseInt(filters.minPrice));
        }
        if (filters.maxPrice) {
            filtered = filtered.filter(p => p.price <= parseInt(filters.maxPrice));
        }

        // City filter
        if (filters.city) {
            filtered = filtered.filter(p => p.city === filters.city);
        }

        // Status filter
        if (filters.status) {
            filtered = filtered.filter(p => p.status === filters.status);
        }

        // Sort
        switch (sortBy) {
            case 'price-low':
                filtered.sort((a, b) => a.price - b.price);
                break;
            case 'price-high':
                filtered.sort((a, b) => b.price - a.price);
                break;
            case 'bedrooms':
                filtered.sort((a, b) => b.bedrooms - a.bedrooms);
                break;
            case 'newest':
            default:
                filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
                break;
        }

        return filtered;
    }, [properties, searchQuery, filters, sortBy]);

    const handleFilterChange = (key, value) => {
        setFilters(prev => ({ ...prev, [key]: value }));
    };

    const clearFilters = () => {
        setSearchQuery('');
        setFilters({
            propertyType: '',
            bedrooms: '',
            minPrice: '',
            maxPrice: '',
            city: '',
            status: 'available'
        });
        setSortBy('newest');
    };

    const hasActiveFilters = searchQuery || filters.propertyType || filters.bedrooms || 
                            filters.minPrice || filters.maxPrice || filters.city || 
                            sortBy !== 'newest';

    const handleRequestClick = (propertyId) => {
        const property = properties.find(p => p._id === propertyId);
        setSelectedProperty(property);
        setModalOpen(true);
        setSuccessMessage('');
        setErrorMessage('');
    };

    const handleRequestSubmit = async (message) => {
        if (!selectedProperty) return;

        setRequestLoading(true);
        setErrorMessage('');

        try {
            const token = localStorage.getItem('token');
            await axios.post(
                'http://localhost:5000/api/requests',
                { propertyId: selectedProperty._id, message },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setSuccessMessage('Request sent successfully!');
            setTimeout(() => {
                setModalOpen(false);
                setSuccessMessage('');
                setSelectedProperty(null);
            }, 1500);
        } catch (err) {
            setErrorMessage(err.response?.data?.message || 'Error sending request. Please try again.');
            setTimeout(() => {
                setErrorMessage('');
            }, 4000);
        } finally {
            setRequestLoading(false);
        }
    };

    const handleCloseModal = () => {
        if (!requestLoading) {
            setModalOpen(false);
            setSelectedProperty(null);
            setErrorMessage('');
            setSuccessMessage('');
        }
    };

    if (loading) return <div className="dashboard-container"><div className="loading">Loading...</div></div>;

    return (
        <>
            <div className="dashboard-container">
                <div className="dashboard-header">
                    <div>
                        <h1>Available Properties</h1>
                        <p>Browse through our selection of verified rental properties</p>
                    </div>
                </div>

                {/* Search and Filter Bar */}
                <div className="properties-filter-bar">
                    <div className="search-container">
                        <FiSearch className="search-icon" />
                        <input
                            type="text"
                            placeholder="Search by title, location, or description..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className="search-input"
                        />
                    </div>

                    <div className="filter-controls">
                        <select
                            value={sortBy}
                            onChange={(e) => setSortBy(e.target.value)}
                            className="sort-select"
                        >
                            <option value="newest">Newest First</option>
                            <option value="price-low">Price: Low to High</option>
                            <option value="price-high">Price: High to Low</option>
                            <option value="bedrooms">Most Bedrooms</option>
                        </select>

                        <button
                            onClick={() => setShowFilters(!showFilters)}
                            className={`filter-toggle-btn ${showFilters ? 'active' : ''}`}
                        >
                            <FiFilter /> Filters
                            {hasActiveFilters && <span className="filter-badge"></span>}
                        </button>
                    </div>
                </div>

                {/* Filter Panel */}
                {showFilters && (
                    <div className="filter-panel">
                        <div className="filter-panel-header">
                            <h3>Filter Properties</h3>
                            <button onClick={clearFilters} className="clear-filters-btn">
                                Clear All
                            </button>
                        </div>

                        <div className="filter-grid">
                            <div className="filter-group">
                                <label>Property Type</label>
                                <select
                                    value={filters.propertyType}
                                    onChange={(e) => handleFilterChange('propertyType', e.target.value)}
                                    className="filter-select"
                                >
                                    <option value="">All Types</option>
                                    <option value="apartment">Apartment</option>
                                    <option value="house">House</option>
                                    <option value="studio">Studio</option>
                                </select>
                            </div>

                            <div className="filter-group">
                                <label>Bedrooms</label>
                                <select
                                    value={filters.bedrooms}
                                    onChange={(e) => handleFilterChange('bedrooms', e.target.value)}
                                    className="filter-select"
                                >
                                    <option value="">Any</option>
                                    <option value="1">1 Bedroom</option>
                                    <option value="2">2 Bedrooms</option>
                                    <option value="3">3 Bedrooms</option>
                                    <option value="4">4+ Bedrooms</option>
                                </select>
                            </div>

                            <div className="filter-group">
                                <label>City</label>
                                <select
                                    value={filters.city}
                                    onChange={(e) => handleFilterChange('city', e.target.value)}
                                    className="filter-select"
                                >
                                    <option value="">All Cities</option>
                                    {cities.map(city => (
                                        <option key={city} value={city}>{city}</option>
                                    ))}
                                </select>
                            </div>

                            <div className="filter-group">
                                <label>Min Price (₹)</label>
                                <input
                                    type="number"
                                    placeholder="Min"
                                    value={filters.minPrice}
                                    onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                                    className="filter-input"
                                />
                            </div>

                            <div className="filter-group">
                                <label>Max Price (₹)</label>
                                <input
                                    type="number"
                                    placeholder="Max"
                                    value={filters.maxPrice}
                                    onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                                    className="filter-input"
                                />
                            </div>

                            <div className="filter-group">
                                <label>Status</label>
                                <select
                                    value={filters.status}
                                    onChange={(e) => handleFilterChange('status', e.target.value)}
                                    className="filter-select"
                                >
                                    <option value="">All</option>
                                    <option value="available">Available</option>
                                    <option value="rented">Rented</option>
                                </select>
                            </div>
                        </div>
                    </div>
                )}

                {/* Results Count */}
                {!loading && (
                    <div className="results-count">
                        Showing {filteredAndSortedProperties.length} of {properties.length} properties
                        {hasActiveFilters && (
                            <button onClick={clearFilters} className="clear-filters-link">
                                Clear filters
                            </button>
                        )}
                    </div>
                )}

                {/* Properties Grid */}
                {filteredAndSortedProperties.length === 0 ? (
                    <div className="empty-state">
                        <h3>No properties found</h3>
                        <p>
                            {hasActiveFilters 
                                ? 'Try adjusting your filters or search query.' 
                                : 'Check back later for new listings.'}
                        </p>
                        {hasActiveFilters && (
                            <button onClick={clearFilters} className="btn-large btn-primary" style={{ marginTop: '20px' }}>
                                Clear All Filters
                            </button>
                        )}
                    </div>
                ) : (
                    <div className="properties-grid">
                        {filteredAndSortedProperties.map(property => (
                            <PropertyCard
                                key={property._id}
                                property={property}
                                onRequest={handleRequestClick}
                            />
                        ))}
                    </div>
                )}
            </div>

            <RequestModal
                isOpen={modalOpen}
                onClose={handleCloseModal}
                onSubmit={handleRequestSubmit}
                propertyTitle={selectedProperty?.title || ''}
                loading={requestLoading}
            />

            {successMessage && (
                <div className="toast toast-success">
                    {successMessage}
                </div>
            )}

            {errorMessage && (
                <div className="toast toast-error">
                    {errorMessage}
                </div>
            )}
        </>
    );
};

export default BrowseProperties;
